import React, {useState} from 'react';
import {useSpring, animated} from 'react-spring';

function Authentication() {
  const [registerFormStatus, setRegisterFormStatus] = useState();

  const loginProps = useSpring({
    left: registerFormStatus ? -430 : 0,
    opacity: registerFormStatus ? 0 : 1
  });

  const registerProps = useSpring({
    left: registerFormStatus ? 0 : 430,
    opacity: registerFormStatus ? 1 : 0
  });

  const loginBtnProps = useSpring({
    borderBottom: registerFormStatus ? 'solid 0px transparent' : 'solid 2px #'
  })

  function loginClicked() {
    setRegisterFormStatus(false);
  };

  function registerClicked() {
    setRegisterFormStatus(true);
  }
  return (
    <div className='login-register-wrapper'>
      <div className='nav-buttons'>
        <button onClick={loginClicked} id='loginBtn' className='active'>Login</button>
        <button onClick={registerClicked} id='registerBtn'>Register</button>
      </div>
      <div className='form-group'>
        <animated.form action='' id='loginForm' style={loginProps}><LoginForm/></animated.form>
        <animated.form action='' id='registrationForm' style={registerProps}><RegistrationForm /></animated.form>
      </div>
      <div className="forgot-panel">
        <a href='/#'>Forgot password</a>
      </div>
    </div>
  )
}

function LoginForm() {
  return (
    <React.Fragment>
      <label for='username'>USERNAME</label>
      <input type='text' id='username' />
      <label for='password'>PASSWORD</label>
      <input type='text' id='password' />
      <input type='submit' value='submit' className='submit' />
    </React.Fragment>
  )
}

function RegistrationForm() {
  return (
    <React.Fragment>
      <label for='fullname'>full name</label>
      <input type='text' id='fullname' />
      <label for='email'>email</label>
      <input type='text' id='email' />
      <label for='password'>password</label>
      <input type='text' id='password' />
      <label for='confirmPassword'>confirm password</label>
      <input type='text' id='confirmPassword' />
      <input type='submit' value='submit' className='submit' />
    </React.Fragment>
  )
}

export default Authentication;
