import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import './SubMenu.css'

const SubMenu = ({item}) => {
  const [subnav, setSubnav] = useState();


  const showSubnav = () => {
    setSubnav(subnav);
  }
  return (
    <>
      <Link to={item.path} onClick={() => item.subNav && showSubnav} className='sidebarlink'>
        <div>
          {item.icon}
          <span className='sidebarlabel'>{item.title}</span>
        </div>
        <div>
          {item.subNav && subnav
            ? item.iconOpened : item.subNav
            ? item.iconClosed : null
          }
        </div>
      </Link>
      {subnav &&
        item.subNav.map((item, index) => {
          return item.subNav != undefined(
            <Link to={item.path} key={index} className='dropdownlink'>
              {item.icon}
              <span>{item.title}</span>
            </Link>
          )
        })
      }
    </>
  )
}

export default SubMenu
